import React, { useEffect, useState } from 'react'
import axios from 'axios';
import Sound from '../sounds/final.mp3'
import moment from 'moment';
import { CiCircleChevRight } from "react-icons/ci";
import Loader from '../components/Loader';

const Questions = ({ match }) => {
    const [loading, setLoading] = useState(true)
    const [data, setData] = useState([])

    const userInfo = JSON.parse(localStorage.getItem('userInfo'))
    useEffect(() => {
        let beat = new Audio(Sound);
        if (window.screenTop && window.screenY) {
            beat.play()
        } else {
            beat.pause()
        }
        axios.post('http://localhost/api/question/getQuestions', { testId: match.params.id, userId: userInfo._id })
            .then(response => {
                setLoading(false)
                setData(response.data.data[0].questionsList)
            })

            .catch(error => console.log(error));
    }, []);

    return (
        <div className='row'>
            {/* {loading ? <Loader /> : ( */}
            <main>
                <div className='testHeader'>
                    <div className='testHead'>
                        <h2 className='test1'>HTML, CSS and CSS with Float</h2>
                        <div className='marks'>
                            <span className='test1'>Total Marks: 50</span>
                            <span className='test1'>Passing Marks: 41</span>
                        </div>
                    </div>

                </div>

                {data.map(a => (
                    <div className='question-text' key={a._id}>
                        <div className='questionText'>
                            <span>Q.1 What is a</span>
                        </div>
                        <div className='options'>
                            <input type='radio' name={a.optionA} id={a.optionA} />
                            <label for={a.optionA}>{a.optionA}</label>
                            <input type='radio' name={a.optionA} id={a.optionB} />
                            <label for={a.optionB}>{a.optionB}</label>
                            <input type='radio' name={a.optionA} id={a.optionC} />
                            <label for={a.optionC}>{a.optionC}</label>
                            <input type='radio' name={a.optionA} id={a.optionD} />
                            <label for={a.optionD}>{a.optionD}</label>
                        </div>

                    </div>
                ))}

            </main>
            {/* )} */}
        </div>
    )
}

export default Questions
