import React, { useEffect, useState } from 'react'
import axios from 'axios';
import moment from 'moment';
import { CiCircleChevRight } from "react-icons/ci";
import Loader from '../components/Loader';

const Questions = ({ match }) => {
    const [loading, setLoading] = useState(true)
    const [data, setData] = useState([])
    const [userOption, setuserOption] = useState()

    const userInfo = JSON.parse(localStorage.getItem('userInfo'))

    useEffect(() => {

        axios.post('http://localhost/api/question/getQuestions', { testId: match.params.id, userId: userInfo._id })
            .then(response => {
                setLoading(false)
                setData(response.data.data[0].questionsList)
            })

            .catch(error => console.log(error));
    }, []);


    console.log(userOption);

    return (
        <div className='row'>
            {/* {loading ? <Loader /> : ( */}
            <main>
                <div className='testHeader'>
                    <div className='testHead'>
                        <h2 className='test1'>HTML, CSS and CSS with Float</h2>
                        <div className='marks'>
                            <span className='test1'>Total Marks: 50</span>
                            <span className='test1'>Passing Marks: 41</span>
                        </div>
                    </div>

                </div>

                {/* {data.map((a, i) => ( */}
                <div className='question-text'>
                    <div className='questionText'>
                        <span>Q.1 {data[0].questionName}</span>
                    </div>
                    <div className='options'>
                        <input onClick={(e) => setuserOption(data[0].optionA)} type='radio' name={data[0].optionA} id={data[0].optionA} />
                        <label for={data[0].optionA}>{data[0].optionA}</label>
                        <input onClick={(e) => setuserOption(data[0].optionB)} type='radio' name={data[0].optionA} id={data[0].optionB} />
                        <label for={data[0].optionB}>{data[0].optionB}</label>
                        <input onClick={(e) => setuserOption(data[0].optionC)} type='radio' name={data[0].optionA} id={data[0].optionC} />
                        <label for={data[0].optionC}>{data[0].optionC}</label>
                        <input onClick={(e) => setuserOption(data[0].optionD)} type='radio' name={data[0].optionA} id={data[0].optionD} />
                        <label for={data[0].optionD}>{data[0].optionD}</label>
                    </div>
                </div>

            </main>
            {/* )} */}
        </div>
    )
}

export default Questions
